mapper.select:
select * from t_user left join t_user_shop shop on t_user.user_id=shop.user_id
where 1=1

    and t_user.user_id=#{userId}


    and t_user.phone like concat(concat('%',#{phone}),'%')


    and t_user.username like concat(concat('%',#{username}),'%')


    and t_user.user_status=#{userStatus,jdbcType=INTEGER}

select * from t_user left join t_user_shop shop on t_user.user_id=shop.user_id
where 1=1

    and t_user.phone = #{phone}


    and t_user.user_status=#{userStatus,jdbcType=INTEGER}

select * from t_user left join t_user_shop shop on t_user.user_id=shop.user_id
where 1=1

    and t_user.user_id=#{userId}


    and t_user.phone like concat(concat('%',#{phone}),'%')


    and t_user.username like concat(concat('%',#{username}),'%')


    and t_user.email like concat(concat('%',#{email}),'%')


    and t_user.user_level=#{userLevel}


    and t_user.user_status=#{userStatus}

order by t_user.user_id

select count(1) count from t_user left join t_user_shop shop on t_user.user_id=shop.user_id
where 1=1

    and t_user.user_id=#{userId}


    and t_user.phone like concat(concat('%',#{phone}),'%')


    and t_user.username like concat(concat('%',#{username}),'%')


    and t_user.email like concat(concat('%',#{email}),'%')


    and t_user.user_level=#{userLevel}


    and t_user.user_status=#{userStatus}

order by t_user.user_id

